// DASHBOARD FUNCIONAL COMPLETO CON COMPONENTES DE PRUEBA Y ESTADO VISIBLE

import React, { useState, useEffect } from 'react';
import './Dashboard.css';

// Simulación de componentes con datos de prueba
const PlaceholderCard = ({ title }) => (
  <div className="card">
    <h3>{title}</h3>
    <p>Contenido de prueba</p>
  </div>
);

const Dashboard = () => {
  const [dataLoaded, setDataLoaded] = useState(false);

  useEffect(() => {
    // Simula tiempo de carga de datos
    setTimeout(() => setDataLoaded(true), 1000);
  }, []);

  return (
    <div className="dashboard light-theme">
      {/* FILA DE KPIs PRINCIPALES */}
      <div className="dashboard-row kpi-row">
        <PlaceholderCard title="Sessions" />
        <PlaceholderCard title="Revenue" />
        <PlaceholderCard title="Avg. Accuracy" />
        <PlaceholderCard title="Retention" />
      </div>

      {/* FILA DEMOGRÁFICA */}
      <div className="dashboard-row">
        <PlaceholderCard title="Age Distribution" />
        <PlaceholderCard title="Gender Distribution" />
        <PlaceholderCard title="Active Users" />
      </div>

      {/* FILA DE PREGUNTAS Y POWERUPS */}
      <div className="dashboard-row">
        <PlaceholderCard title="Trivia Questions" />
        <PlaceholderCard title="Top PowerUps" />
        <PlaceholderCard title="Failed Questions" />
      </div>

      {/* FILA DE ECONOMÍA Y RANKINGS */}
      <div className="dashboard-row">
        <PlaceholderCard title="User Rankings" />
        <PlaceholderCard title="Trading Stats" />
      </div>

      {/* FILA DE TKNs Y CRIPTOMONEDAS */}
      <div className="dashboard-row">
        <PlaceholderCard title="TKNs Earned by Day" />
        <PlaceholderCard title="Top Cryptocurrencies" />
      </div>
    </div>
  );
};

export default Dashboard;
