// NUEVO DISEÑO DEL DASHBOARD CON ADAPTACIÓN DE CARDS

import React from 'react';
import './Dashboard.css';

// Importar componentes (puedes agregarlos desde tu estructura)
import AgeDistribution from './AgeDistribution';
import GenderDistribution from './GenderDistribution';
import RetentionChart from './RetentionChart';
import SessionsCard from './SessionsCard';
import RevenueCard from './RevenueCard';
import QuestionsCard from './QuestionsCard';
import AverageAccuracyCard from './AverageAccuracyCard';
import ActiveUsersCard from './ActiveUsersCard';
import TopPowerUpsCard from './TopPowerUpsCard';
import UserRankingCard from './UserRankingCard';
import TradingStatsCard from './TradingStatsCard';
import TknsByDayCard from './TknsByDayCard';
import TopCryptocurrenciesCard from './TopCryptocurrenciesCard';
import MostFailedQuestionsCard from './MostFailedQuestionsCard';

const Dashboard = () => {
  return (
    <div className="dashboard">
      {/* FILA 1: KPIs compactos */}
      <div className="dashboard-row kpis">
        <SessionsCard />
        <RevenueCard />
        <AverageAccuracyCard />
      </div>

      {/* FILA 2: Demografía */}
      <div className="dashboard-row">
        <AgeDistribution />
        <GenderDistribution />
        <RetentionChart />
      </div>

      {/* FILA 3: Participación */}
      <div className="dashboard-row">
        <ActiveUsersCard />
        <QuestionsCard />
        <TopPowerUpsCard />
      </div>

      {/* FILA 4: Rendimiento y economía */}
      <div className="dashboard-row">
        <UserRankingCard />
        <TradingStatsCard />
      </div>

      {/* FILA 5: Economía detallada */}
      <div className="dashboard-row">
        <TknsByDayCard />
        <TopCryptocurrenciesCard />
      </div>

      {/* FILA 6: Preguntas con más errores (ancho completo) */}
      <div className="dashboard-row full-width">
        <MostFailedQuestionsCard />
      </div>
    </div>
  );
};

export default Dashboard;
