// NUEVO DISEÑO DEL DASHBOARD CON ESTILO MODERNO INSPIRADO EN LIGHT THEME

import React from 'react';
import './Dashboard.css';

// Componentes visualmente modernos y claros
import AgeDistribution from './AgeDistribution';
import GenderDistribution from './GenderDistribution';
import RetentionChart from './RetentionChart';
import SessionsCard from './SessionsCard';
import RevenueCard from './RevenueCard';
import AverageAccuracyCard from './AverageAccuracyCard';

const Dashboard = () => {
  return (
    <div className="dashboard light-theme">
      {/* FILA DE KPIs INSPIRADA EN ESTILO MODERNO */}
      <div className="kpi-section">
        <div className="kpi-card">
          <h3>Final Points</h3>
          <div className="kpi-value highlight">81</div>
          <p>Your final point</p>
        </div>
        <div className="kpi-card">
          <h3>Average</h3>
          <div className="kpi-value">50</div>
          <p>Avg. point</p>
        </div>
        <div className="kpi-card">
          <h3>Highest</h3>
          <div className="kpi-value">131</div>
          <p>Highest point</p>
        </div>
        <div className="kpi-card">
          <h3>Rank</h3>
          <div className="kpi-value">17,303</div>
          <p>Gameweek rank</p>
        </div>
      </div>

      {/* GRÁFICA DE HISTORIAL DE PUNTOS */}
      <div className="point-history-card">
        <h2>Point History</h2>
        <RetentionChart />
      </div>

      {/* KPI adicionales */}
      <div className="stats-row">
        <SessionsCard />
        <RevenueCard />
        <AverageAccuracyCard />
      </div>
    </div>
  );
};

export default Dashboard;
